MODx.msg.confirm({
    title: _('remove_locks')
    ,text: _('confirm_remove_locks')
    ,url: MODx.config.connectors_url
    ,params: {
        action: 'system/remove_locks'
    }
    ,listeners: {
        'success': {fn:function() {
            var tree = Ext.getCmp("modx-resource-tree");
            if (tree && tree.rendered) {
                tree.refresh();
            }
         },scope:this}
    }
});
